const fs = require('fs');
const crypto = require('crypto');

const body = $json.body ?? $json;
const files = Array.isArray(body.files) ? body.files : (body.file ? [body.file] : []);
if (!files.length) throw new Error('No input files provided.');
const sourceLang = body.langIn ?? 'ja';
const targetLang = body.langOut ?? 'ru';
if (sourceLang !== 'ja' || targetLang !== 'ru') throw new Error('Pipeline currently supports only ja->ru.');
const allowedServices = ['openai', 'google', 'ollama'];
const requestedService = (body.service ?? 'openai').toLowerCase();
if (!allowedServices.includes(requestedService)) throw new Error(`Unsupported service: ${requestedService}`);
const serviceChain = [requestedService, ...allowedServices.filter(s => s !== requestedService)];
const safePath = (p) => {
  if (typeof p !== 'string' || !p.trim()) throw new Error('Invalid file path');
  if (!/^[\w@%+=:,./\- ]+$/u.test(p)) throw new Error(`Unsafe path characters: ${p}`);
  return p;
};
const safeOptPath = (p) => p ? safePath(p) : '';
const runStateDir = safePath(body.runStateDir ?? '/data/state');
const hashFile = (filePath) => crypto.createHash('sha256').update(fs.readFileSync(filePath)).digest('hex');

return files.map((file, idx) => {
  const inputFile = safePath(file);
  return {
    json: {
      runId: body.runId ?? `${Date.now()}-${idx}`,
      inputFile,
      inputFileHash: hashFile(inputFile),
      runStartedAt: new Date().toISOString(),
      outputDir: safePath(body.outputDir ?? '/data/output'),
      runStateDir,
      artifactBaseUrl: (body.artifactBaseUrl ?? '').trim(),
      artifactStoragePrefix: safePath(body.artifactStoragePrefix ?? '/artifacts'),
      langIn: sourceLang,
      langOut: targetLang,
      pages: body.pages ?? '',
      poolMaxWorkers: Math.max(1, Math.min(8, Number(body.poolMaxWorkers ?? 2))),
      customPromptPath: safeOptPath(body.customPromptPath ?? ''),
      glossaryPath: safeOptPath(body.glossaryPath ?? ''),
      primaryFontFamily: (body.primaryFontFamily ?? 'Noto Serif').replace(/["`]/g, ''),
      serviceChain,
      serviceIndex: 0,
      currentService: serviceChain[0],
      scannedHint: Boolean(body.scannedHint ?? false),
      allowOcrWorkaround: Boolean(body.allowOcrWorkaround ?? false)
    }
  };
});

const f = $json.inputFile;
if (!/\.pdf$/i.test(f)) throw new Error(`Invalid file extension: ${f}`);
return [{ json: { ...$json } }];

const scannedHint = Boolean($json.scannedHint ?? false);
const allowOcrWorkaround = Boolean($json.allowOcrWorkaround ?? false);
if (scannedHint && !allowOcrWorkaround) {
  return [{ json: { ...$json, scannedBlocked: true, code: 'SCANNED_REQUIRES_OCR', message: 'Scanned PDF detected. Pre-OCR is required before translation.' } }];
}
return [{ json: { ...$json, scannedBlocked: false } }];

const serviceFlagMap = { openai: '--openai', google: '--google', ollama: '--ollama' };
const pagesArg = $json.pages ? ` --pages "${$json.pages}"` : '';
const promptArg = $json.customPromptPath ? ` --custom-system-prompt "${$json.customPromptPath}"` : '';
const glossaryArg = $json.glossaryPath ? ` --glossaries "${$json.glossaryPath}"` : '';
const ocrArg = $json.allowOcrWorkaround ? ' --auto-enable-ocr-workaround' : '';
const serviceFlag = serviceFlagMap[$json.currentService] ?? '--openai';
const cmd = [
  'pdf2zh-next',
  `"${$json.inputFile}"`,
  '--lang-in ja',
  '--lang-out ru',
  '--bilingual',
  serviceFlag,
  `--output "${$json.outputDir}"`,
  `--pool-max-workers ${$json.poolMaxWorkers}`,
  `--primary-font-family "${$json.primaryFontFamily}"`,
  promptArg,
  glossaryArg,
  pagesArg,
  ocrArg
].filter(Boolean).join(' ');
return [{ json: { ...$json, command: cmd } }];

const nextIndex = ($json.serviceIndex ?? 0) + 1;
if (nextIndex >= $json.serviceChain.length) return [{ json: { ...$json, hasFallback: false } }];
return [{ json: { ...$json, hasFallback: true, serviceIndex: nextIndex, currentService: $json.serviceChain[nextIndex] } }];

const serviceFlagMap = { openai: '--openai', google: '--google', ollama: '--ollama' };
const pagesArg = $json.pages ? ` --pages "${$json.pages}"` : '';
const promptArg = $json.customPromptPath ? ` --custom-system-prompt "${$json.customPromptPath}"` : '';
const glossaryArg = $json.glossaryPath ? ` --glossaries "${$json.glossaryPath}"` : '';
const ocrArg = $json.allowOcrWorkaround ? ' --auto-enable-ocr-workaround' : '';
const serviceFlag = serviceFlagMap[$json.currentService] ?? '--ollama';
const cmd = [
  'pdf2zh-next',
  `"${$json.inputFile}"`,
  '--lang-in ja',
  '--lang-out ru',
  '--bilingual',
  serviceFlag,
  '--ignore-cache',
  `--output "${$json.outputDir}"`,
  `--pool-max-workers ${$json.poolMaxWorkers}`,
  `--primary-font-family "${$json.primaryFontFamily}"`,
  promptArg,
  glossaryArg,
  pagesArg,
  ocrArg
].filter(Boolean).join(' ');
return [{ json: { ...$json, fallbackCommand: cmd } }];

const endedAt = new Date().toISOString();
const commandArgs = ($json.command || $json.fallbackCommand || '').replace(/^pdf2zh-next\s*/, '');
return [{ json: { ...$json, audit: { runId: $json.runId, inputFile: $json.inputFile, inputFileHash: $json.inputFileHash, provider: $json.currentService, commandArgs, runStartedAt: $json.runStartedAt, runEndedAt: endedAt, status: 'completed', errorClass: null } } }];

const endedAt = new Date().toISOString();
const commandArgs = ($json.command || $json.fallbackCommand || '').replace(/^pdf2zh-next\s*/, '');
return [{ json: { ...$json, audit: { runId: $json.runId ?? null, inputFile: $json.inputFile ?? null, inputFileHash: $json.inputFileHash ?? null, provider: $json.currentService ?? null, commandArgs, runStartedAt: $json.runStartedAt ?? null, runEndedAt: endedAt, status: 'failed', errorClass: ($json.errorClass || 'translation.provider_chain_exhausted') } } }];

const stderr = String($json.stderr || $json.stdout || $json.message || '').toLowerCase();
const retryablePatterns = [ /timeout/, /timed out/, /429/, /rate limit/, /temporarily unavailable/, /connection reset/, /econnreset/, /econnrefused/, /5\d\d/ ];
const nonRetryablePatterns = [ /invalid file/, /unsupported/, /permission denied/, /no such file/, /not found/, /unsafe path/, /invalid.*path/, /pipeline currently supports only ja->ru/ ];
let errorClass = 'translation.unknown';
let retryable = false;
if (retryablePatterns.some((p) => p.test(stderr))) { errorClass = 'translation.transient'; retryable = true; }
if (nonRetryablePatterns.some((p) => p.test(stderr))) { errorClass = 'translation.non_retryable_input'; retryable = false; }
return [{ json: { ...$json, errorClass, retryable, retryAttempt: Number($json.retryAttempt || 0) } }];

const fs = require('fs');
const path = require('path');
const endedAt = $json.audit?.runEndedAt ?? new Date().toISOString();
const state = {
  runId: $json.runId ?? null,
  inputFile: $json.inputFile ?? null,
  inputFileHash: $json.inputFileHash ?? null,
  status: $json.audit?.status ?? ($json.status || 'unknown'),
  provider: $json.currentService ?? null,
  errorClass: $json.audit?.errorClass ?? ($json.errorClass || null),
  retryAttempt: Number($json.retryAttempt || 0),
  updatedAt: endedAt
};
const dir = $json.runStateDir || '/data/state';
const safeRun = String($json.runId || 'unknown').replace(/[^\w.-]/g, '_');
const safeHash = String($json.inputFileHash || 'unknown').replace(/[^\w.-]/g, '_');
const fileName = `${safeRun}-${safeHash}.json`;
fs.mkdirSync(dir, { recursive: true });
const stateFile = path.join(dir, fileName);
fs.writeFileSync(stateFile, JSON.stringify(state));
return [{ json: { ...$json, runStateFile: stateFile, runState: state } }];

const fs = require('fs');
const path = require('path');
const inputBase = $json.outputNameBase || path.basename($json.inputFile || '', path.extname($json.inputFile || ''));
const outDir = $json.outputDir || '/data/output';
let matched = [];
try {
  const entries = fs.readdirSync(outDir);
  matched = entries
    .filter((name) => /\.pdf$/i.test(name) && name.includes(inputBase))
    .map((name) => {
      const full = path.join(outDir, name);
      const st = fs.statSync(full);
      return { file: full, size: st.size, mtimeMs: st.mtimeMs, fileName: name };
    })
    .filter((x) => x.size > 0)
    .sort((a, b) => b.mtimeMs - a.mtimeMs);
} catch (e) {
  return [{ json: { ...$json, outputIntegrityPassed: false, outputIntegrityMessage: `Output directory check failed: ${e.message}`, errorClass: 'output.integrity_failed', message: `Output directory check failed: ${e.message}` } }];
}
if (!matched.length) {
  return [{ json: { ...$json, outputIntegrityPassed: false, outputIntegrityMessage: `No non-empty output PDF artifact found for base ${inputBase}`, errorClass: 'output.integrity_failed', message: `No non-empty output PDF artifact found for base ${inputBase}` } }];
}
return [{ json: { ...$json, outputIntegrityPassed: true, outputFile: matched[0].file, outputFileName: matched[0].fileName } }];

const path = require('path');
const outputFile = $json.outputFile || '';
const baseUrl = String($json.artifactBaseUrl || '').replace(/\/$/, '');
const storagePrefix = String($json.artifactStoragePrefix || '/artifacts').replace(/\/$/, '');
const fileName = path.basename(outputFile || '') || `${$json.outputNameBase || 'output'}.pdf`;
const storageKey = `${storagePrefix}/${$json.runId || 'run'}/${fileName}`.replace(/\/+/g, '/');
const downloadUrl = baseUrl ? `${baseUrl}${storageKey}` : '';
return [{ json: { ...$json, artifact: { fileName, storageKey, downloadUrl: downloadUrl || null, naming: { outputNameBase: $json.outputNameBase, outputMode: $json.outputMode, outputVersion: $json.outputVersion, sourceStem: $json.sourceStem } } } }];

const path = require('path');
const sourceStem = path.basename($json.inputFile || '', path.extname($json.inputFile || ''));
const runIdSafe = String($json.runId || 'run').replace(/[^\w.-]/g, '_');
const sourceStemSafe = String(sourceStem || 'input').replace(/[^\w.-]/g, '_');
const outputMode = 'bilingual';
const outputVersion = 1;
const outputNameBase = `${runIdSafe}-${sourceStemSafe}-${outputMode}-v${outputVersion}`;
return [{ json: { ...$json, sourceStem, outputMode, outputVersion, outputNameBase } }];
